using namespace std;

class Solution {
public:
	string helper(string s, int l, int r) {
		while (l >= 0 && r < s.size() && s[l] == s[r])
		{
			l -= 1;
			r += 1;
		}
		return s.substr(l + 1, r - l - 1);
	}
	string longestPalindrome(string s) {
		string res;
		for (int i = 0; i < s.size(); ++i)
		{
			string tmp = helper(s, i, i);
			if (tmp.size() > res.size()) res = tmp;

			tmp = helper(s, i, i + 1);
			if (tmp.size() > res.size()) res = tmp;
		}
		return res;
	}
};

int main()
{
	Solution s;
	{
		cout << s.longestPalindrome("") << endl;
	}

	std::cin.get();
	return 0;
}




